"use client";
import { useState } from "react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import "../globals.css";

import {
    Button,
    Image,
    Menu
} from "antd";
import type {
    MenuProps
} from 'antd';
import {
    // AppstoreOutlined,
    BookOutlined,
    HomeOutlined,
    // MailOutlined,
    SettingOutlined
} from '@ant-design/icons';
import "@ant-design/v5-patch-for-react-19";

interface LevelKeysProps {
    key?: string;
    children?: LevelKeysProps[];
}

type MenuItem = Required<MenuProps>['items'][number];

const items: MenuItem[] = [
    {
        key: '1',
        icon: <HomeOutlined />,
        label: (<Link href="/dashboard">仪表盘</Link>)
    },
    {
        key: '2',
        icon: <SettingOutlined />,
        label: (<Link href="/settings">设&nbsp;&nbsp;&nbsp;置</Link>)
    },
    {
        key: '99',
        icon: <BookOutlined />,
        label: (<Link href="/">博客首页</Link>)
    }
];

const getLevelKeys = (items1: LevelKeysProps[]) => {
    const key: Record<string, number> = {};
    const func = (items2: LevelKeysProps[], level = 1) => {
        items2.forEach((item) => {
            if (item.key) {
                key[item.key] = level;
            }
            if (item.children) {
                func(item.children, level + 1);
            }
        });
    };
    func(items1);
    return key;
};

const levelKeys = getLevelKeys(items as LevelKeysProps[]);

export default function AdminLayout({ children }: Readonly<{ children: React.ReactNode; }>) {
    const router = useRouter();
    const [stateOpenKeys, setStateOpenKeys] = useState(['2', '23']);

    const onOpenChange: MenuProps['onOpenChange'] = (openKeys) => {
        const currentOpenKey = openKeys.find((key) => !stateOpenKeys.includes(key));
        // open
        if (currentOpenKey !== undefined) {
            const repeatIndex = openKeys
                .filter((key) => key !== currentOpenKey)
                .findIndex((key) => levelKeys[key] === levelKeys[currentOpenKey]);

            setStateOpenKeys(
                openKeys
                    // remove repeat key
                    .filter((_, index) => index !== repeatIndex)
                    // remove current level all child
                    .filter((key) => levelKeys[key] <= levelKeys[currentOpenKey]),
            );
        } else {
            // close
            setStateOpenKeys(openKeys);
        }
    };

    const handlerLogout = async () => {
        const bb = await fetch("/api/logout", {
            method: "DELETE",
        });
        const data = await bb.json();
        if (data.success) {
            router.push("/login");
        }
    };

    return (
        <>
            <html lang="zh-CN">
                <body>
                    <div id="ADMIN" className="w-ful h-screen flex">

                        <div className="sider w-[210px] bg-white">
                            <div className="h-30 bg-none flex items-center justify-center">
                                <Image
                                    alt="logo"
                                    className="mx-auto rounded-full shadow-sm hover:shadow-md"
                                    fallback="data:image/png;base64,"
                                    height={100}
                                    preview={false}
                                    src="error"
                                    width={100}
                                />
                            </div>
                            <Menu
                                mode="inline"
                                defaultSelectedKeys={['231']}
                                openKeys={stateOpenKeys}
                                onOpenChange={onOpenChange}
                                style={{ width: 210 }}
                                items={items}
                            />
                            <div
                                className="h-10 p-0 flex items-center justify-center gap-1"
                            >
                                <Button
                                    className=""
                                    onClick={handlerLogout}
                                    type="primary"
                                >
                                    登出
                                </Button>
                            </div>
                        </div>

                        <div className="admin-main w-[calc(100%-210px)]">
                            <div className="admin-main-header h-16 bg-slate-50 shaodw-md"></div>
                            <div className="admin-main-content bg-white my-2 mx-4 py-1 px-3 rounded-lg">{children}</div>
                            <div className="admin-main-footer text-center">
                                <p>Created by <Link className="border-b-2" href="https://github.com/ciraos" target="_blank">葱苓sama</Link> with <span className="animate-pulse">❤</span> {new Date().getFullYear()}.</p>
                            </div>
                        </div>

                    </div>
                </body>
            </html>
        </>
    );
}
